class CHOOSE_WEATHER():
    step_name = 'CHOOSE_WEATHER'

    def __init__(self):
        print(f'Registering {self.step_name} step')

    def predict(self,img,weather1,weather2):
        prompt1 = f'Tell me the attributes when the weather is {weather1} in one sentence.'
        prompt2 = f'Tell me the attributes when the weather is {weather2} in one sentence.'
        weather_desc1 = API.gpt3(prompt1, 'gpt3_general')
        weather_desc2 = API.gpt3(prompt2, 'gpt3_general')
        result1 = API.clip(img,weather_desc1)
        result2 = API.clip(img,weather_desc2)
        if result1 > result2:
            result = weather1
        else:
            result = weather2
        return result

    def execute(self,img,weather1,weather2):
        result = self.predict(img,weather1,weather2)
        return result
