class COMPARE_COLOR():
    step_name = 'COMPARE_COLOR'

    def __init__(self):
        print(f'Registering {self.step_name} step')

    def expand_box(self,box,img_size,factor=1.5):
        W,H = img_size
        x1,y1,x2,y2 = box
        dw = int(factor*(x2-x1)/2)
        dh = int(factor*(y2-y1)/2)
        cx = int((x1 + x2) / 2)
        cy = int((y1 + y2) / 2)
        x1 = max(0,cx - dw)
        x2 = min(cx + dw,W)
        y1 = max(0,cy - dh)
        y2 = min(cy + dh,H)
        return [x1,y1,x2,y2]

    def predict(self,img,boxes1,boxes2,obj1,obj2,question):
        if len(boxes1) > 0:
            box1 = boxes1[0]
            box1 = self.expand_box(box1,img.size)
            out_img1 = img.crop(box1)
        else:
            out_img1 = img

        if len(boxes2) > 0:
            box2 = boxes2[0]
            box2 = self.expand_box(box2,img.size)
            out_img2 = img.crop(box2)
        else:
            out_img2 = img

        color1 = API.vqa(out_img1, f'What color is the {obj1}?')
        color2 = API.vqa(out_img2, f'What color is the {obj2}?')

        prompt = f'Can the {color1} be regarded as the same color as {color2}? You should just reply yes or no without any other words.'
        temp = API.gpt3(prompt, 'gpt3_general')

        if 'same' in question.lower():
            if 'yes' in temp.lower():
                result = 'yes'
            elif 'no' in temp.lower():
                result = 'no'
        elif 'different' in question.lower():
            if 'yes' in temp.lower():
                result = 'no'
            elif 'no' in temp.lower():
                result = 'yes'
        else:
            if 'yes' in temp.lower():
                result = 'yes'
            elif 'no' in temp.lower():
                result = 'no'

        return result

    def execute(self,img,boxes1,boxes2,obj1,obj2,question):
        result = self.predict(img,boxes1,boxes2,obj1,obj2,question)
        return result
