class VERIFY_EXISTENCE():
    step_name = 'VERIFY_EXISTENCE'

    def __init__(self):
        print(f'Registering {self.step_name} step')

    def predict(self,img,obj1,obj2,question):
        boxes = API.object_detector(img)
        prompt1 = f"A photo of {obj1}"
        prompt2 = f"A photo of {obj2}"
        flag1, flag2 = False, False
        boxes.append([0,0,img.size[0],img.size[1]])

        for box in boxes:
            out_img = img.crop(box)
            score1 = API.clip(out_img, prompt1)
            score2 = API.clip(out_img, prompt2)
            if score1 > 0.6:
                flag1 = True
            if score2 > 0.6:
                flag2 = True

        if 'and' in question:
            if flag1 == True and flag2 == True:
                result = 'yes'
            else:
                result = 'no'
        else:
            if flag1 == False and flag2 == False:
                result = 'no'
            else:
                result = 'yes'

        return result

    def execute(self,img,obj1,obj2,question):
        result = self.predict(img,obj1,obj2,question)
        return result
