class VERIFY_MATERIAL():
    step_name = 'VERIFY_MATERIAL'

    def __init__(self):
        print(f'Registering {self.step_name} step')

    def expand_box(self,box,img_size,factor=1.5):
        W,H = img_size
        x1,y1,x2,y2 = box
        dw = int(factor*(x2-x1)/2)
        dh = int(factor*(y2-y1)/2)
        cx = int((x1 + x2) / 2)
        cy = int((y1 + y2) / 2)
        x1 = max(0,cx - dw)
        x2 = min(cx + dw,W)
        y1 = max(0,cy - dh)
        y2 = min(cy + dh,H)
        return [x1,y1,x2,y2]

    def predict(self,img,boxes,material,obj,question):
        if len(boxes) > 0:
            prompt = f'Tell me the detailed visual features of the {obj} that is made of {material} in one sentence.'
            object_description = API.gpt3(prompt, 'gpt3_general')
            box = boxes[0]
            box = self.expand_box(box, img.size)
            out_img = img.crop(box)
            result = API.clip(out_img,object_description)
        else:
            result = API.vqa(img,question)
        return result

    def execute(self,img,boxes,material,obj,question):
        result = self.predict(img,boxes,material,obj,question)
        if len(boxes) > 0:
            if result > 0.6:
                flag = True
            else:
                flag = False
        else:
            if 'yes' in result.lower():
                flag = True
            else:
                flag = False
        return flag
